  //           ? 1
  //           : -1
  //       );
  //     } else {
  //       state.products.sort((a, b) => (a.id > b.id ? 1 : -1));
  //     }

  //     return { filteredProducts: state.products };
  //   });
  // };

  // handleChangeSort = (e) => {
  //   this.setState({ sort: e.target.value });
  //   this.listProducts();
  // };

    const setHandleChangeSort = (e) => {
      setSortItems(e => e.target.value)
      // this.setState({ sort: e.target.value });
      this.listProducts();
    };


  return (
    <div>
      <Banner />
      <Container>
        <h1 className="homepage-title">Discover better-for-you Doggos</h1>

        <Row>
          <Col md={9}>
            <Filter
            sort={sort}
            setHandleChangeSort={setHandleChangeSort}
            // count={filteredProducts.length}
            />

            <hr />

            {/* <HomepageOverview state={state} setState={setState}/> */}
            <Products
              filteredProducts={filteredProducts}
              setFilteredProducts={setFilteredProducts}
              products={products}
              setProducts={setProducts}
              // handleAddToCart={this.handleAddToCart}
            />
          </Col>

          <Col>
            {/* <Basket
                // cartItems={this.state.cartItems}
                handleRemoveFromCart={this.handleRemoveFromCart}
              /> */}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Home;
