import React, { useState} from "react";
import { useCartItems } from "./Hooks/useCartItems";
import Product from "../Components/Products";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import util from "../util";


const Cart = () => {
  const [cartItems, setCartItems] = useState(useCartItems());
console.log(cartItems, "loading from cart.js")
  return (
  <Container>

<Row>
<Col xs={7}>
 <Button className="cart__page-left-title">
 ‚Üê Back to Shopping
 </Button>
 <div className="cart__cart-list">
 <div className="cart__line-item">
<div className="cart-item-content-container">
<div className="cart__product-image-container">
<div className="cart__product-button">
<img   className="card-img-top"
          src={`/products/${sku}.jpg`}
          alt={title} />

</div>
</div>

</div>
 </div>
 </div>
</Col>

<Col xs={3}>
  Order Summary
</Col>




</Row>







{cartItems.length === 0 ? (
        "Doggo Basket is empty"
      ) : (
        <div>
          <h3>
            {" "}
            You have {cartItems.length} different{" "}
            {cartItems.length > 1 ? "doggos" : "doggo"} to pet in the
            Doggo Basket.
          </h3>
        </div>
      )}
      {cartItems.length > 0 && (
        <div>
          <ul>
            {cartItems.map((item) => (
              <li key={item.id}>
                <b>{item.title}</b> X {item.count} = $ {item.price * item.count}
                <Button
                  className="button_primary-color"
                  // onClick={() => handleRemoveFromCart(item)}
                >
                  X
                </Button>
              </li>
            ))}
          </ul>
          Total:{" "}
          {util.formatCurrency(
            cartItems.reduce((a, c) => a + c.price * c.count, 0)
          )}
        </div>
      )}
</Container>
  );
};

export default Cart;
