import React, { useState, useEffect } from "react";
import "../App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Products from "./Products";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CardDeck from "react-bootstrap/CardDeck";
import Banner from "./Banner";
import Filter from "./Filter";
// import Basket from "./Basket";

function Home(props) {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [sortValue, setSortValue] = useState("lowest");
  const [count, setCount] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const res = await fetch("http://localhost:8000/products");
      res.json().then((data) => {
        const sorted = sortProducts(data);
        setProducts(sorted);
      });
    };
    fetchData();
  }, []);

  const sortProducts = (list) => {
    let sorted = list;
console.log(sorted, "unsorted list")
    if (sortValue === "lowest") {
      sorted = list.sort((a, b) => b.price - a.price);
      console.log("lowest");
    }
    if (sortValue === "highest") {

      sorted = list.sort((a, b) => b.price - a.price);
      console.log("highest")
    }
    console.log(sorted, "sorted");
    return sorted;
  };



  const handleChangeSort = (e) => {
    setSortValue(e.target.value);
    const sorted = sortProducts(products);
    setProducts(sorted);
  };

  return (
    <div>
      <Banner />
      <Container>
        <h1 className="homepage-title">Discover better-for-you Doggos</h1>

        <Row>
          <Col md={9}>
            <Filter
              sortValue={sortValue}
              handleChangeSort={handleChangeSort}
              // count={filteredProducts.length}
            />

            <hr />

            {/* <HomepageOverview state={state} setState={setState}/> */}
            <Products
              products={products}
              setProducts={setProducts}
              // handleAddToCart={this.handleAddToCart}
            />
          </Col>

          <Col>
            {/* <Basket
                // cartItems={this.state.cartItems}
                handleRemoveFromCart={this.handleRemoveFromCart}
              /> */}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Home;

